import Popover from '../Popover';
import React from 'react';

class InstructorDay extends React.Component {

    getField = (instrDay, index) => {
        if (!instrDay.lessonsThisDay[index])
            return;
        else
            return instrDay.lessonsThisDay[index].student.firstName + " " + instrDay.lessonsThisDay[index].student.lastName;
    }

    getFieldColor = (instrDay, index) => {
        if (instrDay.lessonsThisDay[index] &&
            (instrDay.lessonsThisDay[index].status === "Finished_Unpaid" || instrDay.lessonsThisDay[index].status === "Finished_Paid"))
            return "info";
        else
            return "secondary";
    };

    render() {
        return (

            this.props.instructorDay.map(instrDay => (
                <tr key={instrDay.instructor}>
                    <td >{instrDay.instructor}</td>
                    <td>
                        <Popover data={this.getField(instrDay, 0)} buttonColor={this.getFieldColor(instrDay, 0)} value />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 1)} buttonColor={this.getFieldColor(instrDay, 1)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 2)} buttonColor={this.getFieldColor(instrDay, 2)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 3)} buttonColor={this.getFieldColor(instrDay, 3)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 4)} buttonColor={this.getFieldColor(instrDay, 4)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 5)} buttonColor={this.getFieldColor(instrDay, 5)} />
                    </td>
                    <td>
                        <Popover lessonStatusChange={() => this.props.lessonStatusChange(instrDay.index, 6, "Finished_Paid")} data={this.getField(instrDay, 6)} buttonColor={this.getFieldColor(instrDay, 6)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 7)} buttonColor={this.getFieldColor(instrDay, 7)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 8)} buttonColor={this.getFieldColor(instrDay, 8)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 9)} buttonColor={this.getFieldColor(instrDay, 9)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 10)} buttonColor={this.getFieldColor(instrDay, 10)} />
                    </td>
                    <td>
                        <Popover data={this.getField(instrDay, 11)} buttonColor={this.getFieldColor(instrDay, 11)} />
                    </td>

                </tr>
            ))
        )
    }
}
export default InstructorDay;
