import {
    SAVE_PRICE_TABLE_REQUEST, FETCH_PRICE_TABLE_REQUEST, UPDATE_PRICE_TABLE_REQUEST,
    DELETE_PRICE_TABLE_REQUEST, PRICE_TABLE_SUCCESS, PRICE_TABLE_FAILURE,
    FETCH_ALL_PRICE_TABLES_REQUEST, FETCH_ALL_PRICE_TABLES_SUCCESS,
    FETCH_SEARCH_PRICE_TABLES_REQUEST
} from "./priceTableTypes";
import axios from 'axios';

export const fetchAllPriceTables = (currentPage, size, sortDir, addSelect) => {
    --currentPage;
    return dispatch => {
        // dispatch(fetchAllPriceTablesRequest());???????????????????????????
        axios.get("http://localhost:8080/priceTable-api/list?page=" + currentPage + "&size=" + size + "&sortBy=paymentStatus&sortDir=" + sortDir)
            .then(response => {
                if (addSelect)
                response.data.content.unshift({
                             id: '', lastName: 'Select PriceTable', firstName: '', NrHoursWeek: 0, NrHoursFull: 0, WeekWage: 0
                         });
                dispatch(fetchAllPriceTablesSuccess(response.data.content, response.data.totalPages, response.data.totalElements, sortDir));
            })
            .catch(error => {
                dispatch(priceTableFailure(error.message));
            });
    };
};

const fetchAllPriceTablesRequest = () => {
    return {
        type: FETCH_ALL_PRICE_TABLES_REQUEST
    };
};

export const savePriceTable = priceTable => {
    return dispatch => {
        axios.post("http://localhost:8080/priceTable-api/list", priceTable)
            .then(response => {
                //  dispatch(priceTableSuccess(response.data));
                dispatch(savePriceTableRequest(priceTable));
            })
            .catch(error => {
                console.log(error);
                dispatch(priceTableFailure(error));
            });
    };
};

const savePriceTableRequest = priceTable => {
    return {
        type: SAVE_PRICE_TABLE_REQUEST,
        newItem: priceTable
    };
};

const fetchPriceTableRequest = updatedPriceTable => {
    return {
        type: FETCH_PRICE_TABLE_REQUEST,
        updatedPriceTable: updatedPriceTable
    };
};

export const fetchPriceTable = priceTableId => {
    return dispatch => {
        axios.get("http://localhost:8080/priceTable-api/" + priceTableId)
            .then(response => {
                dispatch(fetchPriceTableRequest(response.data));
                //dispatch(priceTableSuccess(response.data.content));
            })
            .catch(error => {
                dispatch(priceTableFailure(error));
            });
    };
};

const updatePriceTableRequest = priceTable => {
    return {
        type: UPDATE_PRICE_TABLE_REQUEST,
        updatedPriceTable: priceTable
    };
};

export const updatePriceTable = priceTable => {
    //console.log(priceTable);
    priceTable.NrHoursWeek = 0;
    priceTable.NrHoursFull = 0;
    priceTable.WeekWage = 0;
    //console.log(priceTable);

    return dispatch => {
        axios.put("http://localhost:8080/priceTable-api/" + priceTable.id, priceTable)
            .then(response => {
                dispatch(updatePriceTableRequest(priceTable));

                // dispatch(priceTableSuccess(response.data));
            })
            .catch(error => {
                console.log(error);
                dispatch(priceTableFailure(error));
            });
    };
};

const deletePriceTableRequest = priceTableId => {
    return {
        type: DELETE_PRICE_TABLE_REQUEST,
        priceTableId: priceTableId
    };
};

export const deletePriceTable = priceTableId => {
    return dispatch => {
        axios.delete("http://localhost:8080/priceTable-api/list/" + priceTableId)
            .then(response => {
                dispatch(deletePriceTableRequest(priceTableId));
                //console.log(response);
                dispatch(priceTableSuccess(response.data));
            })
            .catch(error => {
                dispatch(priceTableFailure(error));
            });
    };
};


const priceTableSuccess = priceTable => {
    return {
        type: PRICE_TABLE_SUCCESS,
        payload: priceTable
    };
};

const priceTableFailure = error => {
    return {
        type: PRICE_TABLE_FAILURE,
        payload: error
    };
};

const fetchAllPriceTablesSuccess = (priceTables, totalPages, totalElements, sortDirection) => {
    return {
        type: FETCH_ALL_PRICE_TABLES_SUCCESS,
        payload: priceTables,
        totalPages: totalPages,
        totalElements: totalElements,
        sortDirection: sortDirection
    };
};

export const searchPriceTables = (searchedValue, currentPage, priceTablesPerPage) => {
    --currentPage;
    return dispatch => {
        axios.get("http://localhost:8080/priceTable-api/search/" + searchedValue + "?page=" + currentPage + "&size=" + priceTablesPerPage)
            .then(response => {
                dispatch(searchPriceTablesRequest(response.data.content));
            })
            .catch(error => {
                dispatch(priceTableFailure(error.message));
            });
    };
};


const searchPriceTablesRequest = priceTables => {
    return {
        type: FETCH_SEARCH_PRICE_TABLES_REQUEST,
        priceTables: priceTables
    };
};
