import {
    SAVE_INSTRUCTOR_REQUEST, FETCH_INSTRUCTOR_REQUEST, UPDATE_INSTRUCTOR_REQUEST, DELETE_INSTRUCTOR_REQUEST, FETCH_ALL_INSTRUCTORS_REQUEST, FETCH_ALL_INSTRUCTORS_SUCCESS,
    INSTRUCTOR_SUCCESS, INSTRUCTOR_FAILURE
} from "./instructorTypes";

const initialState = {
    instructor: '', error: '', instructors: []
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case SAVE_INSTRUCTOR_REQUEST || FETCH_INSTRUCTOR_REQUEST || UPDATE_INSTRUCTOR_REQUEST || FETCH_ALL_INSTRUCTORS_REQUEST:
            return {
                ...state
            };
        case DELETE_INSTRUCTOR_REQUEST:
            console.log(action);
            return { ...state,
                instructors: state.instructors.filter(item => item.id !== action.instructorId),

                // ...state.instructors.slice(0, action.payload),
                // ...state.instructors.slice(action.payload+1)

            };
        case INSTRUCTOR_SUCCESS:
            return {...state,
                instructor: action.payload,
                error: ''
            };
        case INSTRUCTOR_FAILURE:
            return {...state,
                instructor: '',
                error: action.payload
            };
        case FETCH_ALL_INSTRUCTORS_SUCCESS:
            return {...state,
                instructors: action.payload,
                error: ''
            };
        default: return state;
    }
};

export default reducer;
