import React, { Component } from 'react';
import { Popover, Button, OverlayTrigger } from 'react-bootstrap';
import { faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import { lessonStatusChange } from './Lesson/Schedule';

export default class SuccessToast extends React.Component {

    changeValue = () => {
        this.props.value = "a";
    }
    render() {
        // const toastCss = {
        //     position: 'fixed',
        //     top: '10px',
        //     right: '10px',
        //     zIndex: '1',
        //     boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)'
        // };
        return (
            <>
                <OverlayTrigger
                    showPopover={this.props.showPopover}
                    key="right"
                    placement="right"
                    overlay={
                        <Popover id={`popover-positioned-right`}>
                            <Popover.Title as="h3">Actions</Popover.Title>
                            <Popover.Content>
                                <Button size="sm" variant="outline-info" type="button" onClick={() => {
                                    showPopover = "false";
                                    this.props.lessonStatusChange();
                                }}>
                                    <FontAwesomeIcon icon={faCheck} />
                                </Button>
                                <Button size="sm" variant="outline-danger" type="button">
                                    <FontAwesomeIcon icon={faTimes} />
                                </Button>
                            </Popover.Content>
                        </Popover>
                    }
                >
                    <Button onClick = {() => showPopover = "true"} variant={this.props.buttonColor}>{this.props.data ? this.props.data : ""}</Button>

                </OverlayTrigger>

            </>
        );

    };
}
