import React from 'react';

import axios from 'axios';
import { Link } from 'react-router-dom';
import { Card, Table, ButtonGroup, Button, InputGroup, FormControl } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faWallet, faEdit, faTrash, faUserPlus } from '@fortawesome/free-solid-svg-icons';
import SuccessToast from '../SuccessToast';

class StudentList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            students: []
        };
    }

    componentDidMount() {
        axios.get("http://localhost:8080/student-api/list")
            .then(response => response.data)
            .then(data => {
                this.setState({ students: data });
            });
    }

    deleteStudent = (idStudent) => {
        axios.delete("http://localhost:8080/student-api/list/" + idStudent)
            .then(response => {
                if (response.data != null) {
                    this.setState({ "show": true });
                    setTimeout(() => this.setState({ "show": false }), 3000);
                    this.setState({
                        students: this.state.students.filter(student => student.id !== idStudent)
                    });
                } else {
                    this.setState({ "show": false });
                }
            });
    }


    render() {

        return (
            <div>
                <div style={{ "display": this.state.show ? "block" : "none" }}>
                    <SuccessToast children={{ show: this.state.show, message: "Student Deleted Successfully.", type: "danger" }} />
                </div>
                <Table striped bordered hover variant="dark">
                    <thead>
                        <tr>
                            <th>Last Name</th>
                            <th>First Name</th>
                            <th>ID Card</th>
                            <th>Phone Nr</th>
                            <th>Payment Status</th>
                            <th>Lesson Hrs</th>
                            <th>Unpaid Lessons</th>
                            <th>Money Owing [zl]</th>
                            <th>Payment in Advance [zl]</th>
                            <th>Actions</th>

                        </tr>
                    </thead>


                    <tbody>
                        {this.state.students.length === 0 ?
                            <tr align="center">
                                <td colSpan="10"> No Students in the Data Base</td>
                            </tr> :

                            this.state.students.map((student) => (
                                <tr key={student.id}>
                                    <td>{student.lastName}</td>
                                    <td>{student.firstName}</td>
                                    <td>{student.idCardNr}</td>
                                    <td>{student.telNr}</td>
                                    <td>{student.paymentStatus}</td>
                                    <td>{student.lessonHours}</td>
                                    <td>{student.unpaidLessons}</td>
                                    <td>{student.moneyOwing}</td>
                                    <td>{student.moneyInAdvance}</td>
                                    <td>
                                        <ButtonGroup>
                                            <Button size="sm" variant="outline-success"><Link to={"settleStudent/" + student.id}> <FontAwesomeIcon icon={faWallet} variant="outline-success" /> </Link></Button>
                                            <Button size="sm" variant="outline-primary"><Link to={"editStudent/" + student.id}> <FontAwesomeIcon icon={faEdit} /> </Link></Button>
                                            <Button size="sm" variant="outline-danger" onClick={this.deleteStudent.bind(this, student.id)}> <FontAwesomeIcon icon={faTrash} /> </Button>
                                        </ButtonGroup>
                                    </td>

                                </tr>
                            ))
                        }
                    </tbody>
                </Table>
                <Link to={"add-student"} className="nav-link">
                    <ButtonGroup>
                        <Button size="lg" variant="outline-dark"> <FontAwesomeIcon icon={faUserPlus} /> </Button>
                    </ButtonGroup>
                </Link>

            </div>
        );
    }

}

export default StudentList;
