import './../../style/Style.css';


class Schedule extends React.Component {

    constructor(props) {
        super(props);
        let today = new Date();
        this.state = {
            date: today.getMonth() + 1 + '-' + today.getDate() + '-' + today.getFullYear(),
            instructors: [],
            lessons: [],
