import React from 'react';
import { Form, Card, Col, Button } from 'react-bootstrap'
import { faSave, faUndo } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


class Student extends React.Component {

    constructor(props) {
        super(props);
        this.state = this.initialState;
        this.studentChange = this.studentChange.bind(this);
        this.submitStudent = this.submitStudent.bind(this);
    }

    initialState = {
        lastName: '', firstName: '', IdCard: '', phoneNr: '', paymentStatus: '', lessonHrs: '', unpaidLessons: '', moneyOwing: '', moneyInAdvance: ''
    }

    submitStudent(event) {
        alert(this.state.lastName);
        event.preventDefault();
    }

    resetStudent = () => {
        this.setState(() => this.initialState);
    }

    studentChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    render() {
        const { firstName, lastName, idCard, phoneNr, paymentStatus, lessonHrs, unpaidLessons, moneyOwing, moneyInAdvance } = this.state;
        return (
            <Card className={"border border-dark bg-dark text-white"}>
                <Form onReset={this.resetStudent} onSubmit={this.submitStudent}>
                    <Form.Row>
                        <Form.Group as={Col} controlId="formGridLastName">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control required
                                autoComplete="off"
                                type="test"
                                name="lastName"
                                value={lastName}
                                onChange={this.studentChange}
                                placeholder="Enter Last Name"
                                className={"bg-dark text-white"} />
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridFirstName">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control required
                                autoComplete="off"
                                type="test"
                                name="firstName"
                                value={firstName}
                                onChange={this.studentChange}
                                placeholder=" Enter First Name"
                                className={"bg-dark text-white"} />
                        </Form.Group>
                    </Form.Row>

                    <Form.Row>

                        <Form.Group as={Col} controlId="formGridIdCard">
                            <Form.Label>IdCard</Form.Label>
                            <Form.Control required
                                autoComplete="off"
                                name="IdCard"
                                type="test"
                                value={idCard}
                                onChange={this.studentChange}
                                placeholder="Enter Id Card"
                                className={"bg-dark text-white"} />
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridPhoneNr">
                            <Form.Label>Phone Nr</Form.Label>
                            <Form.Control required
                                autoComplete="off"
                                name="phoneNr"
                                type="test"
                                value={phoneNr}
                                onChange={this.studentChange}
                                placeholder="Enter Phone Nr"
                                className={"bg-dark text-white"} />
                        </Form.Group>
                    </Form.Row >

                    <Form.Row>
                        <Form.Group as={Col} controlId="formGridMoneyInAdvance">
                            <Form.Label>Payment In Advance</Form.Label>
                            <Form.Control required
                                autoComplete="off"
                                name="moneyInAdvance"
                                type="test"
                                value={moneyInAdvance}
                                onChange={this.studentChange}
                                placeholder="$"
                                className={"bg-dark text-white"} />
                        </Form.Group>
                    </Form.Row>



                    <Button size="sm" variant="primary" type="submit">
                        <FontAwesomeIcon icon={faSave} /> Submit
                    </Button>
                    {'      '}

                    <Button size="sm" variant="secondary" type="reset">
                        <FontAwesomeIcon icon={faUndo} />  Reset
                    </Button>
                </Form >


            </Card>
        );
    }

}
export default Student;
