import {SAVE_INSTRUCTOR_REQUEST, FETCH_INSTRUCTOR_REQUEST, UPDATE_INSTRUCTOR_REQUEST,
     DELETE_INSTRUCTOR_REQUEST, INSTRUCTOR_SUCCESS, INSTRUCTOR_FAILURE,
    SAVE_ALL_INSTRUCTORS_REQUEST} from "./instructorTypes";
import axios from 'axios';

export const fetchAllInstructors = ( currentPage, instructorsPerPage,sortDirection ) => {
    return dispatch => {
        dispatch(fetchInstructorRequest());
        axios.get("http://localhost:8080/instructor-api/list?page=" + currentPage + "&size=" + instructorsPerPage + "&sortBy=paymentStatus&sortDir=" + sortDirection)
            .then(response => {
                dispatch(instructorSuccess(response.data));
            })
            .catch(error => {
                dispatch(instructorFailure(error.message));
            });
    };
};

const fetchAllInstructorsRequest = () => {
    return {
        type: FETCH_ALL_INSTRUCTORS_REQUEST
    };
};

export const saveInstructor = instructor => {
    return dispatch => {
        dispatch(saveInstructorRequest());
        axios.post("http://localhost:8080/instructor-api/list", instructor)
            .then(response => {
                dispatch(instructorSuccess(response.data));
            })
            .catch(error => {
                dispatch(instructorFailure(error));
            });
    };
};

const saveInstructorRequest = () => { 
    return {
        type: SAVE_INSTRUCTOR_REQUEST
    };
};

const fetchInstructorRequest = () => {
    return {
        type: FETCH_INSTRUCTOR_REQUEST
    };
};

export const fetchInstructor = instructorId => {
    return dispatch => {
        dispatch(fetchInstructorRequest());
        axios.get("http://localhost:8080/instructor-api/"+instructorId)
            .then(response => {
                dispatch(instructorSuccess(response.data));
            })
            .catch(error => {
                dispatch(instructorFailure(error));
            });
    };
};

const updateInstructorRequest = () => {
    return {
        type: UPDATE_INSTRUCTOR_REQUEST
    };
};

export const updateInstructor = instructor => {
    return dispatch => {
        dispatch(updateInstructorRequest());
        axios.put("http://localhost:8080/instructor-api/", instructor)
            .then(response => {
                dispatch(instructorSuccess(response.data));
            })
            .catch(error => {
                dispatch(instructorFailure(error));
            });
    };
};

const deleteInstructorRequest = () => {
    return {
        type: DELETE_INSTRUCTOR_REQUEST
    };
};

export const deleteInstructor = instructorId => {
    return dispatch => {
        dispatch(deleteInstructorRequest());
        axios.delete("http://localhost:8080/instructor-api/list/"+instructorId)
            .then(response => {
                dispatch(instructorSuccess(response.data));
            })
            .catch(error => {
                dispatch(instructorFailure(error));
            });
    };
};

const instructorSuccess = instructor => {
    return {
        type: INSTRUCTOR_SUCCESS,
        payload: instructor
    };
};

const instructorFailure = error => {
    return {
        type: INSTRUCTOR_FAILURE,
        payload: error
    };
};
