import React from 'react';
import { Card, CardGroup, Row, Col } from 'react-bootstrap';

import './../../style/Style.css';


class Schedule extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            lessons: [],
            schedule: ''
        };
    }

    componentDidMount() {

    }


    render() {


        return (
            <div>

                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header>


                    </Card.Header>
                    <Card.Body>
                        <Table striped bordered hover variant="dark">
                            <thead>
                                <tr>
                                    <th>Instructor</th>
                                    <th>Student</th>
                                    <th onClick={this.sortData}> Date <div className={this.state.sortToggle ? "arrow arrow-down" : "arrow arrow-up"} /> </th>
                                    <th>Time</th>
                                    <th>How Long [h]</th>
                                    <th>Nr of Students</th>
                                    <th>Status</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>


                            <tbody>
                                {this.state.lessons.length === 0 ?
                                    <tr align="center">
                                        <td colSpan="10"> No Lessons in the Data Base</td>
                                    </tr> :

                                    this.state.lessons.map(lesson => (
                                        <tr key={lesson.id}>
                                            <td>{lesson.instructor.firstName + " " + lesson.instructor.lastName}</td>
                                            <td>{lesson.student.firstName + " " + lesson.student.lastName}</td>
                                            <td>{lesson.date}</td>
                                            <td>{lesson.time}</td>
                                            <td>{lesson.howLong}</td>
                                            <td>{lesson.nrStudents}</td>
                                            <td>{lesson.status}</td>


                                        </tr>
                                    ))
                                }
                            </tbody>
                        </Table>
                    </Card.Body>
                    <Card.Footer>



                    </Card.Footer>
                </Card>
            </div>

        );
    }


}

export default Schedule;
