                                                    <Button size="sm" variant="outline-danger" onClick={this.deletePriceTable.bind(this, priceTable.id)}> <FontAwesomeIcon icon={faTrash} /> </Button>
                                                </ButtonGroup>
                                            </td>

                                        </tr>
                                    ))
                                }
                            </tbody>
                        </Table>
                    </Card.Body>
                    <Card.Footer>
                        <div style={{ "float": "left" }}>
                            <Link to={"add-priceTable"} className="nav-link">
                                <ButtonGroup>
                                    <Button size="lg" variant="outline-light"> <FontAwesomeIcon icon={faUserPlus} /> </Button>
                                </ButtonGroup>
                            </Link>
                        </div>


                    </Card.Footer>
                </Card>

            </div>
        );
    }

}

const mapStateToProps = state => {
    return {
        priceTable: state.priceTable,
        priceTables: state.priceTable.priceTables,
        totalPages: state.priceTable.totalPages,
        totalElements: state.priceTable.totalElements,
        currentPage: state.priceTable.currentPage,
        searchedPriceTable: state.priceTable.searchedPriceTable,
        sortDirection: state.priceTable.sortDirection
    }
};


const mapDispatchToProps = dispatch => {
    return {
        fetchAllPriceTables: (currentPage, size, sortDir, addSelect) => dispatch(fetchAllPriceTables(currentPage, size, sortDir, addSelect)),
        deletePriceTable: (priceTableId) => dispatch(deletePriceTable(priceTableId)),
        searchPriceTables: (searchedPriceTable, currentPage, sizePage) => dispatch(searchPriceTables(searchedPriceTable, currentPage, sizePage))
    }

};


export default connect(mapStateToProps, mapDispatchToProps)(PriceTableList);
