import React from 'react';
import { Form, Card, Col, Button } from 'react-bootstrap';
import { faSave, faUndo, faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link } from 'react-router-dom';
import axios from 'axios';
import SuccessToast from '../SuccessToast';

class Student extends React.Component {

    constructor(props) {
        super(props);
        this.state = this.initialState;
        this.state.show = false;
        this.state.method = 'post';
        this.studentChange = this.studentChange.bind(this);
        this.submitStudent = this.submitStudent.bind(this);
    }

    initialState = {
        id: '', lastName: '', firstName: '', idCardNr: '', telNr: '', paymentStatus: 0, lessonHours: 0, unpaidLessons: 0, moneyOwing: 0, moneyInAdvance: ''
    }

    componentDidMount() {
        const idStudent = +this.props.match.params.id;
        if (idStudent) {
            this.findStudentById(idStudent);
        }
    };


    findStudentById = (idStudent) => {
        axios.get("http://localhost:8080/student-api/" + idStudent)
            .then(response => {
                if (response.data != null) {
                    this.setState({
                        id: this.response.data.id,
                        lastName: this.response.data.lastName,
                        firstName: this.response.data.firstName,
                        idCardNr: this.response.data.idCardNr,
                        telNr: this.response.data.telNr,
                        paymentStatus: this.response.data.paymentStatus,
                        lessonHours: this.response.data.lessonHours,
                        unpaidLessons: this.response.data.unpaidLessons,
                        moneyOwing: this.response.data.moneyOwing,
                        moneyInAdvance: this.response.data.moneyInAdvance
                    });
                }
            }).catch((error) => {
                console.error("Error: " + error);
            });
    };

    returnToList = () => {
        return this.props.history.push("/students");
    };

    submitStudent = event => {
        event.preventDefault();

        const student = {
            lastName: this.state.lastName,
            firstName: this.state.firstName,
            idCardNr: this.state.idCardNr,
            telNr: this.state.telNr,
            paymentStatus: this.state.paymentStatus,
            lessonHours: this.state.lessonHours,
            unpaidLessons: this.state.unpaidLessons,
            moneyOwing: this.state.moneyOwing,
            moneyInAdvance: this.state.moneyInAdvance
        };

        axios.post("http://localhost:8080/student-api/list", student)
            .then(response => {
                if (response.data != null) {
                    this.setState({ "show": true });
                    setTimeout(() => this.setState({ "show": false }), 3000);
                } else {
                    this.setState({ "show": false });
                }
            });
        this.setState(this.initialState);

    };

    resetStudent = () => {
        this.setState(() => this.initialState);
    };

    studentChange = event => {
        this.setState({
            [event.target.name]: event.target.value
        });
    };

    render() {
        const { firstName, lastName, idCard, telNr, paymentStatus, lessonHours, unpaidLessons, moneyOwing, moneyInAdvance } = this.state;
        return (
            <div>
                <div style={{ "display": this.state.show ? "block" : "none" }}>
                    <SuccessToast show={this.state.show} message="Student Saved Successfully." type="success" />
                </div>

                <Card className={"border border-dark bg-dark text-white"}>
                    <Form onReset={this.resetStudent} onSubmit={this.submitStudent}>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridLastName">
                                <Form.Label>Last Name</Form.Label>
                                <Form.Control required
                                    autoComplete="off"
                                    type="test"
                                    name="lastName"
                                    value={lastName}
                                    onChange={this.studentChange}
                                    placeholder="Enter Last Name"
                                    className={"bg-dark text-white"} />
                            </Form.Group>

                            <Form.Group as={Col} controlId="formGridFirstName">
                                <Form.Label>First Name</Form.Label>
                                <Form.Control required
                                    autoComplete="off"
                                    type="test"
                                    name="firstName"
                                    value={firstName}
                                    onChange={this.studentChange}
                                    placeholder=" Enter First Name"
                                    className={"bg-dark text-white"} />
                            </Form.Group>
                        </Form.Row>

                        <Form.Row>

                            <Form.Group as={Col} controlId="formGrididCardNr">
                                <Form.Label>id Card Nr</Form.Label>
                                <Form.Control required
                                    autoComplete="off"
                                    name="idCardNr"
                                    type="test"
                                    value={idCard}
                                    onChange={this.studentChange}
                                    placeholder="Enter Id Card"
                                    className={"bg-dark text-white"} />
                            </Form.Group>

                            <Form.Group as={Col} controlId="formGridPhoneNr">
                                <Form.Label>Phone Nr</Form.Label>
                                <Form.Control required
                                    autoComplete="off"
                                    name="telNr"
                                    type="test"
                                    value={telNr}
                                    onChange={this.studentChange}
                                    placeholder="Enter Phone Nr"
                                    className={"bg-dark text-white"} />
                            </Form.Group>
                        </Form.Row >

                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridMoneyInAdvance">
                                <Form.Label>Payment In Advance</Form.Label>
                                <Form.Control required
                                    autoComplete="off"
                                    name="moneyInAdvance"
                                    type="test"
                                    value={moneyInAdvance}
                                    onChange={this.studentChange}
                                    placeholder="$"
                                    className={"bg-dark text-white"} />
                            </Form.Group>
                        </Form.Row>


                        <div>
                            <Button size="sm" variant="primary" type="submit">
                                <FontAwesomeIcon icon={faSave} /> Submit
                    </Button>
                            {'      '}

                            <Button size="sm" variant="secondary" type="reset">
                                <FontAwesomeIcon icon={faUndo} />  Reset
                    </Button>
                        </div>

                        <div>
                            <Button size="sm" variant="light" type="button" onClick={this.returnToList.bind()}>
                                <FontAwesomeIcon icon={faArrowLeft} />  Return
                    </Button>
                        </div>
                    </Form >


                </Card >
            </div>
        );
    }

}
export default Student;
