import React, { Component } from 'react';
import { Popover, Button, OverlayTrigger, ButtonGroup } from 'react-bootstrap';
import { faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default class SuccessToast extends React.Component {

    changeValue = () => {
        this.props.value = "a";
    }
    render() {
        // const toastCss = {
        //     position: 'fixed',
        //     top: '10px',
        //     right: '10px',
        //     zIndex: '1',
        //     boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)'
        // };
        if (this.props.data) {
            return (
                <>

                    <OverlayTrigger
                        trigger="click"
                        key="right"
                        placement="right"
                        overlay={
                            <Popover id={`popover-positioned-right`}>
                                <Popover.Title as="h3">Actions</Popover.Title>
                                <Popover.Content>
                                    <Button size="sm" variant="outline-info" type="button" onClick={() => { this.props.lessonStatusChange() }}>
                                        <FontAwesomeIcon icon={faCheck} />
                                    </Button>
                                    <Button size="sm" variant="outline-danger" type="button" onClick={() => { this.props.deleteLesson() }}>
                                        <FontAwesomeIcon icon={faTimes} />
                                    </Button>

                                </Popover.Content>
                            </Popover>
                        }
                    >

                        <Button variant={this.props.buttonColor}>{this.props.data}</Button>
                    </OverlayTrigger>

                </>
            );
        }
        else {
            return (
                <>

                    <OverlayTrigger
                        trigger="click"
                        key="right"
                        placement="right"
                        overlay={
                            <Popover id={`popover-positioned-right`}>
                                <Popover.Title as="h3">Actions</Popover.Title>

                                <Popover.Content>
                                    <Button show="false" size="lg" variant="outline-danger" block onClick={() => { this.props.deleteLesson() }}>
                                        <FontAwesomeIcon icon={faTimes} />
                                    </Button>
                                </Popover.Content>
                            </Popover>
                        }
                    >
                        <ButtonGroup vertical>
                            <Button size="lg" variant="transparent"></Button>
                            <Button size="lg" variant="transparent"></Button>
                        </ButtonGroup>

                    </OverlayTrigger>

                </>
            );
        }

    };
}
